    def delaFile(self, qModelIndex):
        self.str1 = self.list1[qModelIndex.row()]

    def btn3_fun(self):
        self.textBrowser_6.clear()
        self.textBrowser.clear()
        self.textBrowser_2.clear()
        self.textBrowser_3.clear()
        self.textBrowser_4.clear()
        self.textBrowser_5.clear()
        self.textBrowser_7.clear()
        self.list1.clear()
        # self.plainTextEdit.clear()
        try:
            conn = sqlite3.connect(rf"{self.pathFile}\Jian\drug.db")
            c = conn.cursor()
            sun = c.execute("select NAME, NUM from JIAN ")

            for i in sun:
                self.list1.append(i[0] + "  " + i[1])
            self.slm.setStringList(self.list1)
            self.listView.setModel(self.slm)
            c.close()

            conn = sqlite3.connect(rf"{self.pathFile}\Jian\hospitaNum.db")
            c = conn.cursor()
            sun = c.execute(
                "select ID, NUM from JIAN ")
            for i in sun:
                conn.commit()
                c.execute("DELETE from JIAN where ID ='%s';" % i[0])
                conn.commit()
                c.close()
                break

            conn = sqlite3.connect(rf"{self.pathFile}\Jian\hospitaFile.db")
            c = conn.cursor()
            li = c.execute("select * from JIAN")
            list1 = li.fetchone()
            conn.commit()
            c.close()
            self.id = list1[0]
            self.textBrowser_6.append(list1[0])
            self.textBrowser.append(list1[1])
            self.textBrowser_2.append(list1[3])
            self.textBrowser_3.append(list1[2])
            self.textBrowser_4.append(list1[4])
            # self.textBrowser_5.append(list1[5])
            self.textBrowser_7.append(list1[0])


        except Exception:
            error.Win(0).show_message("提示", "没有病人")

    def btn4_fun(self):
        try:
            self.slm.setStringList(self.list1)
            self.listView.setModel(self.slm)
            self.text = self.textBrowser_5.toPlainText()
            conn = sqlite3.connect(rf"{self.pathFile}\Jian\hospitaFile.db")
            c = conn.cursor()
            sql = """UPDATE JIAN set BINLI = '%s' where  ID = '%s'""" % (self.text, self.id)
            c.execute(sql)
            conn.commit()
            # self.textBrowser_5.append(str1)
            # self.textBrowser_5.clear()
            li = c.execute("select * from JIAN")
            list1 = li.fetchone()
            c.execute("DELETE from JIAN where ID ='%s';" % list1[0])
            conn.commit()
            c.close()

            conn0 = sqlite3.connect(rf"{self.pathFile}\Jian\hospitaFileWc.db")
            c0 = conn0.cursor()
            sql = '''INSERT INTO JIAN (ID, NAME,AGE, SEX, USERID, KESI, BINLI, YP) VALUES ('%s', '%s', '%s', '%s', '%s', '%s', '%s','%s')''' % (
                list1[0], list1[1], list1[2], list1[3], list1[4], list1[5], self.text, self.str1)
            c0.execute(sql)
            conn0.commit()
            conn0.close()
            error.Win(0).show_message("提示", "提交成功")
        except Exception as f:
            pass


