if __name__ == '__main__':
    pathFile = os.path.expanduser("~")
    if not os.path.exists(fr"{pathFile}\Jian"):
        os.mkdir(fr"{pathFile}\Jian")
        conn = sqlite3.connect(rf"{pathFile}\Jian\birthday.db")
        c = conn.cursor()
        c.execute('''CREATE TABLE JIAN
                                            (NAME TEXT PRIMARY KEY   NOT NULL,
                                             DATA           TEXT  NOT NULL,
                                             THE             TEXT   NOT NULL);''')
        conn.commit()
        str2 = datetime.datetime.now().strftime('%Y-%m-%d %H:%M:%S')
        sql = '''INSERT INTO JIAN (NAME, DATA, THE) VALUES ('%s', '%s', '%s')''' % (pathFile, str2, "小简科技")
        c.execute(sql)
        conn.commit()
        conn.close()
    app = QApplication(sys.argv)
    m = QMainWindow()
    # 设置窗口尺寸
    Ui_MainWindow().setupUi(m)
    # 加载控件
    m.show()
